import React from 'react';
import '../Css/MobileFilter.css';

const MobileFilter = ({
  selectedBrand,
  selectedPrice,
  onBrandChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <h1>Product Filters</h1>
    <div className="filter-section">
      <div className='brand-section'>
      <label htmlFor="brand">By Brand:</label>
      <img   src='https://rukminim2.flixcart.com/fk-p-flap/240/240/image/059c763612908e31.jpg?q=90' alt='' value=''></img>
      <img src='' alt='' value=''></img>
      <img src='' alt='' value=''></img>
      <img src='' alt='' value=''></img>
      <img src='' alt='' value=''></img>
      <img src='' alt='' value=''></img>
      <select id="brand" value={selectedBrand} onChange={onBrandChange}>
        <option value="">All Brands</option>
        <option value="Oppo">Oppo</option>
        <option value="Apple">Apple</option>
        {/* Add more brand options as needed */}
      </select>
    </div>
    </div>
    <div className="filter-section">
      <label>By Price:</label>
      <input
        type="range"
        id="price"
        min="0"
        max="100000"
        step="500"
        value={selectedPrice === null ? 0 : selectedPrice}
        onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
      />
      <div className="price-label">
        {selectedPrice} /-
      </div>
    </div>
  </div>
);

// MobileFilter.defaultProps = {
//   selectedPrice: 100000
// };

export default MobileFilter;
