import React from 'react';

const MobileFilter = ({ selectedBrand, selectedPrice, onBrandChange, onPriceChange }) => (
  <div>
    <h1>Product Filters</h1>
    <div>
      <label htmlFor="brand">By Brand:</label>
      <select id="brand" value={selectedBrand} onChange={onBrandChange}>
        <option value="">All Brands</option>
        <option value="Brand1">Opp</option>
        <option value="Brand2">Brand 2</option>
        {/* Add more brand options as needed */}
      </select>
    </div>
    <div>
      <label htmlFor="price">By Price:</label>
      <input
        type="number"
        id="price"
        placeholder="Enter Price"
        value={selectedPrice}
        onChange={onPriceChange}
      />
    </div>
  </div>
);

export default MobileFilter;
