import React from 'react';
import '../Css/MobileFilter.css';
import all from '../Images/5110770.png'
import Laptops from '../Images/laptop.png';
import Desktops from '../Images/desktop.png';
import accessories from '../Images/accessories.png'
import smartwatch from '../Images/smartwatches.png';
import Camera from '../Images/Camera.png';
import headphones from '../Images/headphones.png';

const FashionFilter = ({
  selectedSubCategory,
  selectedPrice,
  onSubCategoryChange,
  onPriceChange
}) => (
  <div className="product-filter">
    <div className="filter-section-brand">
      <div className='brand-section'>
        <img
          className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`}
          src={all}
          alt='all'
          onClick={() => onSubCategoryChange('')}
        />

        <img
          className={`brand-image ${selectedSubCategory === 'Laptop' ? 'selected' : ''}`}
          src={Laptops}
          alt='laptop'
          onClick={() => onSubCategoryChange('laptop')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'Desktop' ? 'selected' : ''}`}
          src={Desktops}
          alt='desktop'
          onClick={() => onSubCategoryChange('desktop')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'accessories' ? 'selected' : ''}`}
          src={accessories}
          alt='accessories'
          onClick={() => onSubCategoryChange('accessories')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'headphones' ? 'selected' : ''}`}
          src={headphones}
          alt='headphones'
          onClick={() => onSubCategoryChange('headphones')}
        />
        <img
          className={`brand-image ${selectedSubCategory === 'smartwatches' ? 'selected' : ''}`}
          src={women_access}
          alt='Womens_accessories'
          onClick={() => onSubCategoryChange('Womens_accessories')}
        />
      </div>

      <div className="filter-section-price">
        <label>Set Price Range:</label>
        <input
          type="range"
          id="price"
          min="0"
          max="100000"
          step="500"
          value={selectedPrice === null ? 0 : selectedPrice}
          onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
        />
        <div className="price-label">
          {selectedPrice} /-
        </div>
      </div>
    </div>
  </div >
);


export default FashionFilter;
