import React from 'react';

function MobileFilter({ companyNames, selectedCompany, setSelectedCompany, logoUrls }) {
  return (
    <div className="filter-section">
      <span>Filter by Company:</span>
      {companyNames.map(companyName => (
        <div
          key={companyName}
          className={`company-filter ${selectedCompany === companyName ? 'active' : ''}`}
          onClick={() => setSelectedCompany(companyName)}
        >
          <img
            src={logoUrls[companyName] || 'default-logo.png'} // Provide a default logo URL
            alt={companyName}
            className="company-logo"
          />
          <span>{companyName}</span>
        </div>
      ))}
    </div>
  );
}

export default MobileFilter;
