import React, { Component } from 'react';
import FilterResult from './FilterComponents/FilterResult';
import '../HomeComponent/Css/DealsDisplay.css';
import API from '../API_Config';
import FashionFilter from './FilterComponents/FashionFilter';

class Electronics extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedSubCategory: '',
      selectMinPrice:0,
      selectMaxPrice: 200000,
      products: [],
      error: null,
    };
  }

  componentDidMount() {
    
    this.fetchProducts();
  }

  // Function to fetch products based on filters
  fetchProducts = () => {
    const { selectedSubCategory, selectMinPrice,selectMaxPrice } = this.state;
    let apiUrl = `${API}/api/product/byCategory?category=Fashion`;

    // Construct the API URL based on filter selections
    const filterParams = [];

    if (selectedSubCategory) {
      filterParams.push(`subcategory=${selectedSubCategory}`);
    }

    if (selectMinPrice !== null) {
      filterParams.push(`minPrice=${selectMinPrice}`);
    }


    if (selectMaxPrice !== null) {
      filterParams.push(`maxPrice=${selectMaxPrice}`);
    }

    if (filterParams.length > 0) {
      apiUrl += `&${filterParams.join('&')}`;
    }

    console.log(apiUrl);

    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        this.setState({ products: data, error: null });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        this.setState({ error });
      });
  };


  handleSubCategoryChange = (brand) => {
    this.setState({ selectedSubCategory: brand }, () => {
      this.fetchProducts();
    });
  };

  // Handle price filter change
  handlePriceChange = (value) => {
    this.setState({ selectMinPrice : value ,selectMaxPrice : value}, () => {
      this.fetchProducts();
    });
  };

  render() {
    const { selectedSubCategory, selectMinPrice,selectMaxPrice, products, error } = this.state;

    return (
      <div>
        <FashionFilter
          selectedSubCategory={selectedSubCategory}
          selectMinPrice={selectMinPrice}
          selectMaxPrice={selectMaxPrice}
          onSubCategoryChange={this.handleSubCategoryChange}
          onPriceChange={this.handlePriceChange}
          onApplyPriceFilter={this.h}
        />
        {error ? (
          <p>Error fetching products: {error.message}</p>
        ) : (
          <FilterResult products={products} />
        )}
      </div>
    );
  }
}

export default Electronics;
