import React from 'react';
import { Carousel } from 'react-carousel-minimal';

function ItemCarousel() {
  const captionStyle = {
    fontSize: '1.60em',
    fontWeight: 'bold',
    color: 'red'
  };
  const slideNumberStyle = {
    fontSize: '20px',
    fontWeight: 'bold',
    color: 'red'
  };

  const newsData = data.map(item => ({
    id: item.newsId,
    image: item.imageUrl,
    caption: item.title,
  }));

  const handleClick = (newsId) => {
   alert(newsId);
  };

  return (
    <div className="App">
      <div style={{ textAlign: "center" }}>
        <div style={{
          padding: "0 20px"
        }}>
          <Carousel
            itemsToShow={1}
            data={newsData}
            time={5000}
            width="850px"
            height="300px"
            captionStyle={captionStyle}
            radius="5px"
            slideNumber={true}
            slideNumberStyle={slideNumberStyle}
            captionPosition="bottom"
            automatic={true}
            dots={true}
            pauseIconColor="red"
            pauseIconSize="40px"
            slideImageFit="cover"
            style={{
              textAlign: "center",
              maxWidth: "850px",
              maxHeight: "600px",
              margin: "5px auto",
            }}
            onClick={(index) => handleClick(newsData[index].id)}
          >
          </Carousel>
        </div>
      </div>
    </div>
  );
}

export default ItemCarousel;
