import React from 'react'
import MobileFilter from './FilterComponents/'

function Mobiles() {

  const [mobiles, setMobiles] = useState([
    { brand: 'apple', price: 600, name: 'iPhone 12' },
    { brand: 'samsung', price: 550, name: 'Galaxy S21' },
    // Add more mobile data
  ]);

  const [filteredMobiles, setFilteredMobiles] = useState(mobiles);

  const handleFilterChange = (filteredMobiles) => {
    setFilteredMobiles(filteredMobiles);
  };
  return (
    <div className="App">
      <h1>Mobile List</h1>
      <MobileFilter mobiles={mobiles} onFilterChange={handleFilterChange} />
      <ul>
        {filteredMobiles.map((mobile) => (
          <li key={mobile.name}>
            {mobile.brand} {mobile.name} - ${mobile.price}
          </li>
        ))}
      </ul>
    </div>
  )
}

export default Mobiles
