import React, { useEffect, useState } from 'react';

function MobileFilter({ mobiles, onFilterChange }) {
  const [selectedBrand, setSelectedBrand] = useState('all');
  const [selectedPrice, setSelectedPrice] = useState(500);
  const [selectedProductType, setSelectedProductType] = useState('Mobile');

  const handleBrandChange = (event) => {
    setSelectedBrand(event.target.value);
  };

  const handlePriceChange = (event) => {
    setSelectedPrice(event.target.value);
  };

  const handleProductTypeChange = (event) => {
    setSelectedProductType(event.target.value);
  };

  useEffect(() => {
    const filteredMobiles = mobiles.filter((mobile) => {
      const brandMatch =
        selectedBrand === 'all' || mobile.brand === selectedBrand;
      const priceMatch = mobile.price <= selectedPrice;
      const productTypeMatch =
        selectedProductType === 'Mobile'

      return brandMatch && priceMatch && productTypeMatch;
    });

    onFilterChange(filteredMobiles);
  }, [selectedBrand, selectedPrice, selectedProductType, mobiles, onFilterChange]);

  return (
    <div className="filter-container">
      <div className="filter-title">Filters</div>
      <div className="filter-section">
        <label htmlFor="brand-filter">Brand:</label>
        <select
          id="brand-filter"
          value={selectedBrand}
          onChange={handleBrandChange}
        >
          <option value="all">All</option>
          <option value="apple">Apple</option>
          <option value="samsung">Samsung</option>
          {/* Add other brand options as needed */}
        </select>
      </div>
      <div className="filter-section">
        <label htmlFor="price-filter">Price:</label>
        <input
          type="range"
          id="price-filter"
          min="0"
          max="100000"
          value={selectedPrice}
          onChange={handlePriceChange}
        />
        <output htmlFor="price-filter">${selectedPrice}</output>
      </div>
  
    </div>
  );
}

export default MobileFilter;
