import React, { Component } from 'react';

class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: '',
      searchResults: [],
    };
  }

  handleSearch = () => {
    // Split user input into words
    const keywords = this.state.userInput.split(' ');

    // Build conditions for each keyword
    const conditions = keywords.map(keyword => {
      return `LOWER(itemTitle) LIKE '%${keyword.toLowerCase()}%'`;
    });

    // Construct the SQL query
    const query = `SELECT * FROM product WHERE ${conditions.join(' OR ')}`;
