import React from 'react';

const WhatsAppShare = ({ productUrl }) => {
  const shareOnWhatsApp = () => {
    const url = `https://api.whatsapp.com/send?text=${encodeURIComponent(productUrl)}`;
    window.open(url, '_blank');
  };

  const shareOnTelegram = () => {
    const url = `https://t.me/share/url?url=${encodeURIComponent(productUrl)}`;
    window.open(url, '_blank');
  };

  return (
    <div>
      <button onClick={shareOnWhatsApp}>Share on WhatsApp</button>
      <button onClick={shareOnTelegram}>Share on Telegram</button>
    </div>
  );
};

export default WhatsAppShare;
