import React, { useState } from 'react';
import SearchResult from './SearchResult'; // Import the SearchResult component.

const SearchBar = () => {
  const [userInput, setUserInput] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    if (userInput.trim() === '') {
      return;
    }
    setLoading(true);
    try {
      const response = await fetch(`/api/product/search?query=${userInput}`);
      const data = await response.json();
      
      // Split the search results word by word and store them in an array.
      const words = data.split(' ');
      setSearchResults(words);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={userInput}
        onChange={handleInputChange}
      />
      <button onClick={handleSearch}>Search</button>
      <SearchResult searchResults={searchResults} /> 
    </div>
    
  );
};

export default SearchBar;
