import React from 'react';
import '../Css/ElectronicsFilter.css';
import all from '../Images/5110770.png';
import MensSports from '../Images/MensSports.png';
import WomensSports from '../Images/WomenSports.png';
import games from '../Images/Sports.png';

const SportsFilter = ({
  selectedSubCategory,
  onSubCategoryChange,
  onPriceChange
}) => 
{
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(200000);
  const handleApply = () => {
    onPriceChange({minPrice, maxPrice });
  };

  return(
  <div className="product-filter">
    <div className='brand-section'>
      <div className="filter-option">
        <div className={`brand-image ${selectedSubCategory === '' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('')}>
          <img className="filter-image" src={all} alt="all" />
        </div>
        <div className="filter-text">
          <span className="option">All Sports</span>
        </div>
      </div>
      <div className="filter-option">
        <div className={`brand-image ${selectedSubCategory === 'MensSportswear' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('MensSportswear')}>
          <img className="filter-image" src={MensSports} alt="Mens Sportswear" />
        </div>
        <div className="filter-text">
          <span className="option">Mens Sportswear</span>
        </div>
      </div>

      <div className="filter-option">
        <div className={`brand-image ${selectedSubCategory === 'WomensSportswear' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('WomensSportswear')}>
          <img className="filter-image" src={WomensSports} alt="Womens Sportswear" />
        </div>
        <div className="filter-text">
          <span className="option">Womens Sportswear</span>
        </div>
      </div>
      <div className="filter-option">
        <div className={`brand-image ${selectedSubCategory === 'Game' ? 'selected' : ''}`} onClick={() => onSubCategoryChange('Game')}>
          <img className="filter-image" src={games} alt="Game Accessories" />
        </div>
        <div className="filter-text">
          <span className="option">Game Accessories</span>
        </div>
      </div>
    </div>

    <div className="filter-section-price">
      <label>Set Price Range:</label>
      <input
      className='price-bar'
        type="range"
        id="price"
        min="0"
        max="100000"
        step="500"
        value={selectedPrice === null ? 0 : selectedPrice}
        onChange={(e) => onPriceChange(parseInt(e.target.value, 10))}
      />
      <div className="price-label">
        {selectedPrice} /-
      </div>
    </div>
  </div>
);
};
export default SportsFilter;
