// Mobiles.js
import React, { useState, useEffect } from 'react';

const Mobiles = ({ filters }) => {
  const [mobiles, setMobiles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch mobile data from the backend API using filters
    // Replace 'apiEndpoint' with your actual API endpoint
    fetch(`apiEndpoint?company=${filters.company}&priceRange=${filters.priceRange}`)
      .then((response) => response.json())
      .then((data) => {
        setMobiles(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [filters]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="mobile-list">
      {mobiles.map((mobile) => (
        <div key={mobile.id} className="mobile-item">
          <img src={mobile.image} alt={mobile.name} />
          <h2>{mobile.name}</h2>
          <p>Price: {mobile.price}</p>
        </div>
      ))}
    </div>
  );
};

export default Mobiles;
